    #1 Combien y a-t-il de handle dans cette array ?
    def calc_length(arr)
        nb_of_handles = arr.length
        puts "Cette liste a #{nb_of_handles} handles"
    end 
    

    #2 Quel est le handle le plus court de cette liste ?
        # Order the array by lenght
        # Take the first element
    def sorting_array(arr)
        ordered_arr_by_length = arr.sort_by(&:length)
        puts "Quel est le handle le plus court de la liste : #{ordered_arr_by_length[0]}"
    end


    #3 Combien y-a-t'il de handles contenant 5 caractères (le @ ne compte pas pour un caractère)
    def count_ch(arr, count)
        count_temp = count + 1
        names_with_i_ch = arr.count {|x| x.length == count_temp}
        puts "Dans la liste de journalistes, il y en a #{names_with_i_ch} avec #{count} caractères"
    end


    #4 Combien commencent par une majuscule (première lettre juste après le @) ?
    def start_by_AZ(arr)
        names_first_upp = arr.count {|x| x[1].match?(/[A-Z]/)}
        puts "Dans la liste de journalistes, il y en a  #{names_first_upp} qui commencent par une lettre majuscule"
    end


    #5 Trie la liste de handle par ordre alphabétique
    def order_az(arr)
        ordered_array = arr.sort_by {|x| x.downcase}
        puts " "
        puts "Trier la liste par ordre alphabétique: "
        puts ordered_array
        puts ""
    end


